topics.style:
.topic {
  height: 450px;
  padding-top: 0 !important;
  background-image: url(https://storage.googleapis.com/mirrormedia-files/assets/images/20201225110708-df252dd6001a8d84309ce139542b0958.jpg);
}

.topic .topic-title {
  position: relative;
  top: 20px;
  left: auto;
  transform: none;
  width: 256px;
  height: 131px;
  margin: 0 auto;
  background-image: url(https://storage.googleapis.com/mirrormedia-files/assets/images/20201225110822-51e79eb99e8b15226b01c30cb2df7af1.png);
  background-size: contain;
}

.leading {
  position: relative;
  top: 30px;
  display: flex;
  justify-content: center;
  align-items: center;
  height: calc(100% - 160px);
}

@media only screen and (min-width: 600px) {
  .topic {
    height: 500px;
  }
}

@media only screen and (min-width: 900px) {
  .topic {
    height: 800px !important;
  }

  .topic .topic-title {
    position: relative;
    top: 10px;
    transform: translate(0%, 0);
    width: 500px;
    height: 300px;

  }

  .leading {
    top: -50px;
    height: calc(100% - 250px);
  }
}

@media only screen and (min-width: 1500px) {
.topic {
    height: 900px !important;
  }
.groupList {
  /* 文章區塊背景色 */
  background-color: #DCD2BE !important;
}

.groupListBlockContainer h1:before {
  /* 分類圖示顏色 (左右側) */
  background-color: #d8c9b2 !important;
}

.groupListBlockContainer h1:after {
  /* 分類圖示顏色 (中間) */
  background-color: #d2c1a3 !important;
}

.groupListBlockContainer h1, .groupListBlock__content h2 a {
  /* 分類標題及文章標題文字顏色 */
  color: #544741 !important;
}

.groupListBlock__content p a {
  /* 文章前言文字顏色 */
  color: #333 !important;
}

.groupListBlock {
  /* 文章區塊底線顏色 */
  border-bottom-color: #aa9987 !important;
}

@media (min-width: 600px) {
  .groupListBlockContainer > div:last-of-type {
    /* 文章區塊底線顏色 */
    border-bottom-color: #aa9987 !important;
  }
}

